import './App.css';
import Titles from "./components/Titles";
import Form from "./components/Form";
import Weather from "./components/Weather";


const API_KEY = "1bf4aee8b109a62081092033b5e3c8a6";

class App extends React.Component{

  getWeather = async (e) => {

    e.preventDefault
    const API_CALL = await fetch(`http://api.openweathermap.org/data/2.5/weather?q=agra,india&appid=${API_KEY}
    &units=metric`);
    const data = await API_CALL.json();
  }
  render(){
  return (
    <div className="App">
      < Titles />
      <Form />
      <Weather />
    </div>
  );
  }
}

export default App;
